{
  "query": "mutation createBooking($guestName: String, $tableSize: Int, $arrivalDate: String, $arrivalTime: String, $status: String, $contact: String) { createBooking(guestName: $guestName, tableSize: $tableSize, arrivalDate: $arrivalDate, arrivalTime: $arrivalTime, status: $status, contact: $contact) { id guestName tableSize arrivalDate arrivalTime status contact } }",
  "variables": {
      "guestName": "John Doe",
      "contact": "1234567890",
      "tableSize": 10,
      "arrivalTime": "1545", 
      "arrivalDate": "2025-02-19", 
      "status": "pending"
    }
}

{
    "query": "mutation updateBookingById($id: String,  $bookingUpdate:BookingUpdateInput) { updateBookingById(id: $id,bookingUpdate: $bookingUpdate) { id guestName tableSize arrivalTime status contact arrivalDate } }",
    "variables": {
        "_id": "67b6114aa330364216ab898c",
        "bookingUpdate": {
            "status": "cancel",
            "arrivalTime": "1500", 
            "arrivalDate": "2025-02-20"
        }
    }
}

{
    "query": "query listAllbookingByStatusDate($arrivalDate: String, $status: String) { listAllbookingByStatusDate(arrivalDate: $arrivalDate, status: $status) { id guestName tableSize arrivalDate status contact } }",
    "variables": {
        "arrivalDate": "2025-02-20", 
        "status": "cancel" 
    }
}

{
    "query": "query ListAllBooking { listAllBooking { id guestName tableSize arrivalDate arrivalTime status contact } }"

}

{
  "query": "query listAllBookingByContactNumber($guestName: String, $contact: String) { listAllBookingByContactNumber(guestName: $guestName, contact: $contact) { id guestName tableSize arrivalTime status contact } }",
  "variables": {
    "contact": "12345671",
    "guestName":"test3"
  }
}
